import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import moment from 'moment';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';


class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = { 
      userAvailableDishes: 3,
      dishes: [],
      scheduledDishes: []
    };
  }

  componentDidMount() {
    fetch('https://testapi.io/api/sajhu/dishes')
        .then( res => res.json())
        .then((data)=> {
            this.setState({
                dishes: data
            });
        })
  }  

  scheduleDish = (dish) => {
    console.log(moment.utcOffset('-0500'));
    if(this.state.scheduledDishes.length < 3) {
      let scheduledDishes = this.state.scheduledDishes;
      scheduledDishes.push(dish);
      alert('Dish scheduled successfuly');
    } else {
      alert('You cant schedule more than 3 dishes.')
    }
  }


  render() {
    return (
      <Grid container>
        {this.state.dishes.map(dish => (
          <Grid item xs={12} sm={3}>
            <Card style={{maxWidth: 300}}>
              <CardMedia
                image={dish.photoUrl}
                title={dish.name}
                style={{height: 140}}
              />
              <CardContent>
                <Typography gutterBottom variant="h5" component="h2">
                  {dish.name}
                </Typography>
                <Typography variant="body2" color="textSecondary" component="p">
                  {dish.shortDescription}
                </Typography>
              </CardContent>
              <CardActions>
                <Button size="small" color="primary">
                  Schedule Dish
                </Button>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>
    );
  }
}

export default App;
